// ============================================================================
// COMPONENTI
// ============================================================================
const Toast = ({ message, type, onClose }) => {
  useEffect(() => {
    const timer = setTimeout(onClose, 3000);
    return () => clearTimeout(timer);
  }, [onClose]);
  return <div className={`toast ${type}`}>{message}</div>;
};

const ImageModal = ({ imageUrl, onClose }) => {
  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <button className="modal-close" onClick={onClose}>Chiudi</button>
        <img src={imageUrl} alt="Task" className="modal-image" />
      </div>
    </div>
  );
};

// ============================================================================
// APP PRINCIPALE CON FIREBASE
// ============================================================================
export default function TaskApp() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [isRegister, setIsRegister] = useState(false);
  const [error, setError] = useState('');
  const [toast, setToast] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [users, setUsers] = useState([]);
  const [filter, setFilter] = useState('all');
  const [userFilter, setUserFilter] = useState('all');
  const [isFormOpen, setIsFormOpen] = useState(false);
  const [newTask, setNewTask] = useState({
    title: '',
    description: '',
    dueDate: '',
    priority: 'medium',
    assignedTo: ''
  });
  const [taskImage, setTaskImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const [modalImage, setModalImage] = useState(null);

  // Auth listener
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
      setLoading(false);
    });
    return () => unsubscribe();
  }, []);

  // Listener tasks in tempo reale
  useEffect(() => {
    if (!user) return;

    const q = query(collection(db, 'tasks'), orderBy('createdAt', 'desc'));
    const unsubscribe = onSnapshot(q, (snapshot) => {
      const tasksData = [];
      snapshot.forEach((doc) => {
        tasksData.push({ id: doc.id, ...doc.data() });
      });
      setTasks(tasksData);
    });

    return () => unsubscribe();
  }, [user]);

  // Listener users in tempo reale
  useEffect(() => {
    if (!user) return;

    const unsubscribe = onSnapshot(collection(db, 'users'), (snapshot) => {
      const usersData = [];
      snapshot.forEach((doc) => {
        usersData.push({ uid: doc.id, ...doc.data() });
      });
      setUsers(usersData);
    });

    return () => unsubscribe();
  }, [user]);

  const showToast = useCallback((message, type = 'success') => {
    setToast({ message, type });
  }, []);

  const handleRegister = async (e) => {
    e.preventDefault();
    const name = e.target.name.value;
    const email = e.target.email.value;
    const password = e.target.password.value;

    if (!name || !email || !password) {
      setError('Compila tutti i campi');
      return;
    }

    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);

      // Salva info utente in Firestore
      await addDoc(collection(db, 'users'), {
        uid: userCredential.user.uid,
        name,
        email,
        createdAt: serverTimestamp()
      });

      setError('');
      showToast(`Account creato! Benvenuto, ${name}!`);
    } catch (err) {
      if (err.code === 'auth/email-already-in-use') {
        setError('Email gi registrata');
      } else {
        setError(err.message);
      }
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const email = e.target.email.value;
    const password = e.target.password.value;

    try {
      await signInWithEmailAndPassword(auth, email, password);
      setError('');
      showToast('Login effettuato!');
    } catch (err) {
      setError('Email o password non corrette');
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      showToast('Logout effettuato');
    } catch (err) {
      showToast('Errore durante il logout', 'error');
    }
  };

  const handleImageUpload = async (file) => {
    if (file && file.type.startsWith('image/')) {
      if (file.size > 5 * 1024 * 1024) {
        showToast('Immagine troppo grande (max 5MB)', 'error');
        return;
      }

      // Anteprima locale
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
      setTaskImage(file);
    } else {
      showToast('Carica solo file immagine', 'error');
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = () => setIsDragging(false);

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);
    handleImageUpload(e.dataTransfer.files[0]);
  };

  const handleAddTask = async (e) => {
    e.preventDefault();
    if (!newTask.title.trim()) {
      showToast('Inserisci un titolo', 'error');
      return;
    }

    try {
      let imageUrl = null;

      // Upload immagine su Firebase Storage se presente
      if (taskImage) {
        const storageRef = ref(storage, `task-images/${Date.now()}_${taskImage.name}`);
        await uploadBytes(storageRef, taskImage);
        imageUrl = await getDownloadURL(storageRef);
      }

      // Trova il nome dell'utente corrente
      const currentUserData = users.find(u => u.uid === user.uid);
      const userName = currentUserData?.name || user.email;

      // Crea task in Firestore
      await addDoc(collection(db, 'tasks'), {
        title: newTask.title,
        description: newTask.description,
        dueDate: newTask.dueDate,
        priority: newTask.priority,
        assignedTo: newTask.assignedTo,
        completed: false,
        createdBy: user.uid,
        createdByName: userName,
        createdAt: serverTimestamp(),
        image: imageUrl
      });

      // Reset form
      setNewTask({ title: '', description: '', dueDate: '', priority: 'medium', assignedTo: '' });
      setTaskImage(null);
      setImagePreview(null);
      setIsFormOpen(false);
      showToast('Task creata con successo!');
    } catch (err) {
      showToast('Errore durante la creazione della task', 'error');
      console.error(err);
    }
  };

  const handleToggleTask = async (taskId, currentStatus) => {
    try {
      const taskRef = doc(db, 'tasks', taskId);
      await updateDoc(taskRef, {
        completed: !currentStatus
      });
    } catch (err) {
      showToast('Errore durante l\'aggiornamento', 'error');
    }
  };

  const handleDeleteTask = async (taskId) => {
    try {
      await deleteDoc(doc(db, 'tasks', taskId));
      showToast('Task eliminata');
    } catch (err) {
      showToast('Errore durante l\'eliminazione', 'error');
    }
  };

  const getFilteredTasks = () => {
    let filtered = tasks;

    // Filter by status
    if (filter === 'all') {
      filtered = filtered.filter(t => !t.completed);
    } else if (filter === 'active') {
      filtered = filtered.filter(t => !t.completed);
    } else if (filter === 'completed') {
      filtered = filtered.filter(t => t.completed);
    } else if (filter === 'overdue') {
      const today = new Date().toISOString().split('T')[0];
      filtered = filtered.filter(t => !t.completed && t.dueDate && t.dueDate < today);
    }

    // Filter by user
    if (userFilter !== 'all') {
      if (userFilter === 'my-tasks') {
        filtered = filtered.filter(t => t.createdBy === user.uid || t.assignedTo === user.uid);
      } else {
        filtered = filtered.filter(t => t.assignedTo === userFilter);
      }
    }

    return filtered;
  };

  const getStats = () => {
    const total = tasks.filter(t => !t.completed).length;
    const completed = tasks.filter(t => t.completed).length;
    const today = new Date().toISOString().split('T')[0];
    const overdue = tasks.filter(t => !t.completed && t.dueDate && t.dueDate < today).length;
    const active = total;

    return { total, completed, overdue, active };
  };

  const getAssignedUserName = (uid) => {
    const foundUser = users.find(u => u.uid === uid);
    return foundUser ? foundUser.name : 'Non assegnato';
  };

  if (loading) {
    return (
      <>
        <style dangerouslySetInnerHTML={{ __html: styles }} />
        <div className="auth-page">
          <div className="auth-card">
            <div className="auth-logo">
              <img src={LOGO_BASE64} alt="The Real Tracker" />
            </div>
            <p>Caricamento...</p>
          </div>
        </div>
      </>
    );
  }

  if (!user) {
    return (
      <>
        <style dangerouslySetInnerHTML={{ __html: styles }} />
        <div className="auth-page">
          <div className="auth-card">
            <div className="auth-logo">
              <img src={LOGO_BASE64} alt="The Real Tracker" />
            </div>
            <p>{isRegister ? 'Crea il tuo account' : 'Accedi con il tuo account'}</p>
            {error && <div className="error-message">{error}</div>}
            <form onSubmit={isRegister ? handleRegister : handleLogin}>
              {isRegister && (
                <div className="form-group">
                  <label>Nome Utente</label>
                  <input type="text" name="name" placeholder="Inserisci il tuo nome" required />
                </div>
              )}
              <div className="form-group">
                <label>Email</label>
                <input type="email" name="email" placeholder="Inserisci la tua email" required />
              </div>
              <div className="form-group">
                <label>Password</label>
                <input type="password" name="password" placeholder="Inserisci la password (min 6 caratteri)" required minLength="6" />
              </div>
              <button type="submit" className="btn">
                {isRegister ? 'Registrati' : 'Accedi'}
              </button>
              <button
                type="button"
                className="btn btn-secondary"
                style={{ marginTop: '10px' }}
                onClick={() => {
                  setIsRegister(!isRegister);
                  setError('');
                }}
              >
                {isRegister ? 'Hai gi un account? Accedi' : 'Non hai un account? Registrati'}
              </button>
            </form>
          </div>
        </div>
      </>
    );
  }

  const stats = getStats();
  const filteredTasks = getFilteredTasks();
  const currentUserData = users.find(u => u.uid === user.uid);
  const userName = currentUserData?.name || user.email;

  return (
    <>
      <style dangerouslySetInnerHTML={{ __html: styles }} />
      <div className="app-container">
        <header className="header">
          <div className="header-logo">
            <img src={LOGO_BASE64} alt="The Real Tracker" />
          </div>
          <div className="header-right">
            <span className="user-email">{userName}</span>
            <button className="btn btn-small btn-secondary" onClick={handleLogout}>Logout</button>
          </div>
        </header>

        <div className="dashboard">
          <div className="statistics">
            <div className="stat-card">
              <h3>{stats.total}</h3>
              <p>Totale Tasks</p>
            </div>
            <div className="stat-card">
              <h3>{stats.completed}</h3>
              <p>Completate</p>
            </div>
            <div className="stat-card">
              <h3>{stats.overdue}</h3>
              <p>Scadute</p>
            </div>
            <div className="stat-card">
              <h3>{stats.active}</h3>
              <p>In Corso</p>
            </div>
          </div>

          <div className="add-task-container">
            <button 
              className={`add-task-btn ${isFormOpen ? 'open' : ''}`}
              onClick={() => setIsFormOpen(!isFormOpen)}
            >
              +
            </button>

            {isFormOpen && (
              <div className="task-form">
                <h2> Crea Nuova Task</h2>
                <form onSubmit={handleAddTask}>
                  <div className="form-row">
                    <div className="form-group">
                      <label>Titolo*</label>
                      <input type="text" placeholder="Titolo della task" value={newTask.title}
                        onChange={(e) => setNewTask({ ...newTask, title: e.target.value })} required />
                    </div>
                    <div className="form-group">
                      <label>Scadenza</label>
                      <input type="date" value={newTask.dueDate}
                        onChange={(e) => setNewTask({ ...newTask, dueDate: e.target.value })} />
                    </div>
                    <div className="form-group">
                      <label>Priorit</label>
                      <select value={newTask.priority}
                        onChange={(e) => setNewTask({ ...newTask, priority: e.target.value })}>
                        <option value="low"> Bassa</option>
                        <option value="medium"> Media</option>
                        <option value="high"> Alta</option>
                      </select>
                    </div>
                  </div>
                  <div className="form-group">
                    <label>Descrizione</label>
                    <textarea rows="3" placeholder="Descrivi la task..." value={newTask.description}
                      onChange={(e) => setNewTask({ ...newTask, description: e.target.value })} />
                  </div>
                  <div className="form-group">
                    <label>Assegna a</label>
                    <select value={newTask.assignedTo}
                      onChange={(e) => setNewTask({ ...newTask, assignedTo: e.target.value })}>
                      <option value="">Nessuno</option>
                      {users.map(u => <option key={u.uid} value={u.uid}>{u.name}</option>)}
                    </select>
                  </div>
                  <div className={`upload-area ${isDragging ? 'dragging' : ''}`}
                    onDragOver={handleDragOver} onDragLeave={handleDragLeave} onDrop={handleDrop}
                    onClick={() => document.getElementById('imageInput').click()}>
                    <input id="imageInput" type="file" accept="image/*" style={{ display: 'none' }}
                      onChange={(e) => handleImageUpload(e.target.files[0])} />
                    {imagePreview ? (
                      <>
                        <img src={imagePreview} alt="Preview" className="image-preview-upload" />
                        <p>Click per cambiare immagine</p>
                      </>
                    ) : (
                      <>
                        <p> Trascina un'immagine qui o click per caricare</p>
                        <p style={{ fontSize: '12px', color: '#999', marginTop: '10px' }}>PNG, JPG, GIF (max 5MB)</p>
                      </>
                    )}
                  </div>
                  <div className="form-row">
                    <button type="submit" className="btn btn-success"> Crea Task</button>
                    <button type="button" className="btn btn-secondary"
                      onClick={() => {
                        setIsFormOpen(false);
                        setNewTask({ title: '', description: '', dueDate: '', priority: 'medium', assignedTo: '' });
                        setImagePreview(null);
                        setTaskImage(null);
                      }}>
                       Annulla
                    </button>
                  </div>
                </form>
              </div>
            )}
          </div>

          <div className="filters">
            <button className={`filter-btn ${filter === 'all' ? 'active' : ''}`}
              onClick={() => setFilter('all')}>
               Tutte
            </button>
            <button className={`filter-btn ${filter === 'active' ? 'active' : ''}`}
              onClick={() => setFilter('active')}>
               In Corso
            </button>
            <button className={`filter-btn ${filter === 'completed' ? 'active' : ''}`}
              onClick={() => setFilter('completed')}>
               Completate
            </button>
            <button className={`filter-btn ${filter === 'overdue' ? 'active' : ''}`}
              onClick={() => setFilter('overdue')}>
               Scadute
            </button>
            <div className="user-filter">
              <label>Filtra per utente:</label>
              <select value={userFilter} onChange={(e) => setUserFilter(e.target.value)}>
                <option value="all">Tutti</option>
                <option value="my-tasks">Le mie tasks</option>
                {users.map(u => <option key={u.uid} value={u.uid}>{u.name}</option>)}
              </select>
            </div>
          </div>

          <div className="task-list">
            {filteredTasks.length === 0 ? (
              <div className="empty-state">
                <h3> Nessuna task</h3>
                <p>{filter === 'completed' ? 'Non ci sono task completate' : 'Crea la tua prima task!'}</p>
              </div>
            ) : (
              filteredTasks.map(task => (
                <div key={task.id} className={`task-card ${task.completed ? 'completed' : ''}`}>
                  <input type="checkbox" className="task-checkbox" checked={task.completed}
                    onChange={() => handleToggleTask(task.id, task.completed)} />
                  {task.image && (
                    <img src={task.image} alt="Task" className="task-image-preview"
                      onClick={() => setModalImage(task.image)} />
                  )}
                  <div className="task-content">
                    <div className="task-header">
                      <h3 className="task-title" style={{ textDecoration: task.completed ? 'line-through' : 'none' }}>
                        {task.title}
                      </h3>
                      <div className="task-actions">
                        <button className="btn-icon btn-delete" onClick={() => handleDeleteTask(task.id)}>
                          
                        </button>
                      </div>
                    </div>
                    {task.description && <p className="task-description">{task.description}</p>}
                    <div className="task-meta">
                      {task.dueDate && <span> {task.dueDate}</span>}
                      <span className={`priority-badge priority-${task.priority}`}>
                        {task.priority === 'high' && ' Alta'}
                        {task.priority === 'medium' && ' Media'}
                        {task.priority === 'low' && ' Bassa'}
                      </span>
                      {task.assignedTo && (
                        <span className="assigned-badge"> {getAssignedUserName(task.assignedTo)}</span>
                      )}
                      <span style={{ fontSize: '12px', color: '#999' }}>Creata da: {task.createdByName}</span>
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>

        {modalImage && <ImageModal imageUrl={modalImage} onClose={() => setModalImage(null)} />}
        {toast && <Toast message={toast.message} type={toast.type} onClose={() => setToast(null)} />}
      </div>
    </>
  );
}